  `mail` varchar(55) NOT NULL,
  `birthdate` date NOT NULL,
  `role` varchar(11) NOT NULL DEFAULT 'customer',
  `password` varchar(70) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

